class Body{constructor(t,e,i,o=5){this.centre=t,this.radius=e,this.mass=i,this.speed=o;const s=random(TWO_PI);this.velocity=createVector(cos(s),sin(s)).mult(this.speed)}draw(){stroke(200),fill(100),circle(this.centre.x,this.centre.y,2*this.radius)}update(){this.centre.add(this.velocity),(this.centre.x+this.radius>width||this.centre.x-this.radius<0)&&(this.velocity.x=-this.velocity.x),(this.centre.y+this.radius>height||this.centre.y-this.radius<0)&&(this.velocity.y=-this.velocity.y),this.draw()}}const bgColor="#070831",primaryColor="#fff4d6",secondaryColor="#908a76";let body1=null,body2=null,bodySqDist=0;function windowResized(){background(bgColor)}function setup(){createCanvas(800,800),body1=new Body(createVector(200,200),80,2),body2=new Body(createVector(80,80),60,5)}function draw(){background(220),collisionCheck(body1,body2),body1.update(),body2.update()}function collisionCheck(t,e){createVector(t.centre.x,t.centre.y).sub(e.centre).magSq()<=Math.pow(t.radius+e.radius,2)&&collisionResolution(t,e)}function collisionResolution(t,e){let i=t.velocity.copy();t.velocity=e.velocity.copy(),e.velocity=i}