const bgColor="#070831",primaryColor="#fff4d6",secondaryColor="#908a76";let xCentre,yCentre,randXOffset,randYOffset,rowDivide=16,colDivide=9,points=[],pDensity=1;function windowResized(){background(bgColor)}function setup(){createCanvas(1280,720),xCentre=width/rowDivide/2,yCentre=height/colDivide/2,strokeWeight(5),noFill();for(let e=0;e<rowDivide;e++){points[e]=[];for(let i=0;i<colDivide;i++){let t=e*(width/rowDivide),o=i*(height/colDivide);randXOffset=random(width/rowDivide),randYOffset=random(height/colDivide),points[e][i]=createVector(t+randXOffset,o+randYOffset)}}}function draw(){for(let e=0;e<width;e++)for(let i=0;i<height;i++){let t=checkNeighbors(getGridCoords(e,i),createVector(e,i));stroke(map(t,0,5e3,255,7),map(t,0,5e3,244,8),map(t,0,5e3,214,49)),point(e,i)}}function getGridCoords(e,i){return createVector(e/(width/rowDivide),i/(height/colDivide))}function sqrdDist(e,i){return(e.x-i.x)*(e.x-i.x)+(e.y-i.y)*(e.y-i.y)}function checkNeighbors(e,i){let t=width*width;for(let o=-1;o<=1;o++)for(let r=-1;r<=1;r++){if(o+e.x<0||o+e.x>=rowDivide)continue;if(r+e.y<0||r+e.y>=colDivide)continue;let n=points[floor(e.x+o)][floor(e.y+r)];t=min(t,sqrdDist(i,n))}return t}